  //点亮整颗
  var LightEntire = function(el,options){
    this.$el = $(el);
    this.$item = this.$el.find('.rating-item');
    this.opts = options;
  };
  LightEntire.prototype.init = function(){
    this.lightOn(this.opts.num);
    if(!this.opts.readOnly){
        this.bindEvent();
    }
  };
  LightEntire.prototype.lightOn = function(num){
    num = parseInt(num);
    this.$item.each(function(index){
        if(index<num){
           $(this).css('background-position','0 -40px');
        }else{
           $(this).css('background-position','0 0');
        }
     });
  };
  LightEntire.prototype.bindEvent = function(){
    var self = this,
        itemLength = self.$item.length;
    self.$el.on('mouseover','.rating-item',function(){
        var num = $(this).index()+self.add;
        self.lightOn(num);

        //短路符，先判断传入的是否函数。call改变this指向
        (typeof self.opts.select === 'function') && self.opts.select.call(this ,num, itemLength)
        //触发select事件
        self.$el.trigger('select',[num,itemLength])
    }).on('click','.rating-item',function(){
       self.opts.num = $(this).index()+self.add;

       (typeof self.opts.chosen === 'function') && self.opts.chosen.call(this ,self.opts.num, itemLength)

         self.$el.trigger('chosen',[self.opts.num,itemLength])
    }).on('mouseout',function(){
        self.lightOn(self.opts.num);
    })
  }

  //点亮半颗
  var LightHalf = function(el,options){
    this.$el = $(el);
    this.$item = this.$el.find('.rating-item');
    this.opts = options;
    this.add = 1;
  };
  LightHalf.prototype.init = function(){
    this.lightOn(this.opts.num);
    if(!this.opts.readOnly){
        this.bindEvent();
    }
  };
  LightHalf.prototype.lightOn = function(num){
    var count = parseInt(num),
        isHalf = count!==num;

    this.$item.each(function(index){
        if(index<num){
           $(this).css('background-position','0 -40px');
        }else{
           $(this).css('background-position','0 0');
        }
     });

    if(isHalf){
            this.$item.eq(count).css('background-position','0 -80px')
    }
  };
  LightHalf.prototype.bindEvent = function(){
    var self = this,
        itemLength = self.$item.length;

    self.$el.on('mousemove','.rating-item',function(e){
        var $this = $(this),
            num = 0;

        if(e.pageX-$this.offset().left < $this.width()/2){//半颗
            self.add = 0.5;
        }else{//整颗
            self.add = 1;
        }
        num = $this.index() + self.add;

        self.lightOn(num);

        //短路符，先判断传入的是否函数。call改变this指向
        (typeof self.opts.select === 'function') && self.opts.select.call(this ,num, itemLength)
        //触发select事件
        self.$el.trigger('select',[num,itemLength])

    }).on('click','.rating-item',function(){
       self.opts.num = $(this).index() + self.add;

       (typeof self.opts.chosen === 'function') && self.opts.chosen.call(this ,self.opts.num, itemLength)

       self.$el.trigger('chosen',[self.opts.num,itemLength])
    }).on('mouseout',function(){
        self.lightOn(self.opts.num);
    })
  }

  //默认参数
  var defaults = {
    mode: 'LightEntire',//半颗 LightHalf
    num: 0,
    readOnly: false,
    select: function(){},
    chosen: function(){}
  };
  //做个映射
  var mode = {
    'LightEntire' : LightEntire,
    'LightHalf' : LightHalf
  }

  //初始化
  var init = function(el,options){
    options = $.extend({},defaults,options);
    if(!mode[options.mode]){//容错
        options.mode = 'LightEntire';
    }
    //new LightEntire(el,options).init();
    //new LightHalf(el,options).init();
    new mode[options.mode](el,options).init();
  };

  return {
    init: init
  }
})();

