          newArr.push(item3)
        }
        for(let j = 0; j<len; j++){ // |||
          item.push(arr[j][n])
        }
        newArr.push(item)
      })
      newArr.map(k=>{
        flag = flag && sum == k.reduce((a,b)=>a+b)
      })
      return flag
}
