    var Rating = function(el,options){
        this.$el = $(el);
        this.opts = $.extend({},Rating.DEFAULTS, options);

        //
        this.opts.total *= 2;
        this.opts.num *= 2;

        this.itemWidth = 33/2;//
        this.displayWidth = this.opts.num*this.itemWidth;//
    };
    Rating.DEFAULTS = {
        total : 5,
        num : 2,
        readOnly : false,
        select : function(){},
        chosen : function(){} 
