            num = $(this).index()+1
        }).on('mouseout',function(){
            lightOn($item,num);
        })
    }

    //jQuery
    $.fn.extend({
        rating: function(num){
            return this.each(function(){
                init(this,num);    
            })
        }
    });

    return {
        init: init
    }


