var rating = (function(){
    // 
    //LightEntire.prototype = new Light();
    // 
    var extend = function(subClass,superClass){
        var F = function(){};//
        F.prototype = superClass.prototype;
        subClass.prototype = new F();
        subClass.prototype.construtor = subClass;
    }


  // 
  var Light = function(el,options){
    this.$el = $(el);
    this.$item = this.$el.find('.rating-item');
    this.opts = options;
    this.add = 1;
    this.selectEvent = 'mouseover';
  };
  Light.prototype.init = function(){
        this.lightOn(this.opts.num);
        if(!this.opts.readOnly){
            this.bindEvent();
        }
  };
  Light.prototype.lightOn = function(num){
    num = parseInt(num);
    this.$item.each(function(index){
        if(index<num){
           $(this).css('background-position','0 -40px');
        }else{
           $(this).css('background-position','0 0');
        }
     });
  };
  Light.prototype.bindEvent = function(){
    var self = this,
        itemLength = self.$item.length;
    //
    self.$el.on(this.selectEvent,'.rating-item',function(e){
        var $this = $(this),
            num = 0;

        //
        self.select(e,$this);

        num = $(this).index()+self.add;
        self.lightOn(num);

        //callthis
        (typeof self.opts.select === 'function') && self.opts.select.call(this ,num, itemLength)
        //select
        self.$el.trigger('select',[num,itemLength])
    }).on('click','.rating-item',function(){
       self.opts.num = $(this).index()+self.add;

       (typeof self.opts.chosen === 'function') && self.opts.chosen.call(this ,self.opts.num, itemLength)

       self.$el.trigger('chosen',[self.opts.num,itemLength])
    }).on('mouseout',function(){
       self.lightOn(self.opts.num);
    })
  };
  Light.prototype.select = function(){
    throw new Error('');
  };
  Light.prototype.unbindEvent = function(){
    this.$el.off();
  }

  //
  var LightEntire = function(el,options){
    Light.call(this,el,options)
    this.selectEvent = 'mouseover';
  };
  extend(LightEntire,Light);//
  LightEntire.prototype.lightOn = function(num){
    Light.prototype.lightOn.call(this,num)
  };
  LightEntire.prototype.select = function(){
    self.add = 1
  }


  //
  var LightHalf = function(el,options){
    Light.call(this,el,options);
    this.selectEvent = 'mousemove';
  };
  extend(LightHalf,Light);//
  LightHalf.prototype.lightOn = function(num){
    var count = parseInt(num),
        isHalf = count!==num;

    Light.prototype.lightOn.call(this,count)

    if(isHalf){
       this.$item.eq(count).css('background-position','0 -80px')
    }
  };
  LightHalf.prototype.select = function(e,$this){
    if(e.pageX-$this.offset().left < $this.width()/2){//
        this.add = 0.5;
    }else{//
        this.add = 1;
    }
  };


  //
  var defaults = {
    mode: 'LightEntire',// LightHalf
    num: 0,
    readOnly: false,
    select: function(){},
    chosen: function(){}
  };
  //
  var mode = {
    'LightEntire' : LightEntire,
    'LightHalf' : LightHalf
  }

  //
  var init = function(el,option){
    var $el = $(el),
        rating = $el.data('rating'),
        options = $.extend({},defaults,typeof option === 'object' && option);
    if(!mode[options.mode]){//
        options.mode = 'LightEntire';
    }
    //new LightEntire(el,options).init();
    //new LightHalf(el,options).init();
    //
    if(!rating){
        $el.data('rating',(rating = new mode[options.mode](el,options)));
        rating.init();
    }
    //option
    if(typeof option === 'string') rating[option]()

  };

    //jQuery
    $.fn.extend({
        rating: function(option){
            return this.each(function(){
                init(this,option);
            })
        }
    });


  return {
    init: init
  }
})();

